import {useEffect, useState} from "react";

export default function Message({message, loggedUser}) {

    const [sender, setSender] = useState()

    useEffect(() => {
        loggedUser._id === message.sender[0] ? setSender(loggedUser.username) : setSender(`l'altro`)
    }, [])


    return (
        <>

            <p>{message.sender[0]}: {message.text}</p>
        </>

    )
}
