import {useEffect, useState} from "react";
import axios from "axios";

export default function Chat({chat, loggedUserId}) {

    const [username, setUsername] = useState(``)
    const [otherId, setOtherId] = useState(`64916faaa21bc72643de44d0`)


    useEffect(() => {
        axios.get("http://localhost:3000/api/users/findUser/" + otherId)
            .then((res) => {
                if (res.status === 200) {
                    return res.data
                } else {
                    throw new Error("Errore di comunicazione col server nell'ottenimento delle chat")
                }
            })


    }, [])


    return (<>
        {chat.utente1 === loggedUserId ? <h2>{username}</h2> : <h2>{username}</h2>}
    </>)

}
