import {useEffect, useState} from "react";
import axios from "axios";

export default function Chat({chat, loggedUserId}) {

    const [username, setUsername] = useState(``)
    const [otherId, setOtherId] = useState(``)
    if (chat.utente1 === loggedUserId) {
        setOtherId(chat.utente2)
    } else {
        setOtherId(chat.utente1)
    }
    console.log(`here: ${otherId}`)


    useEffect(() => {
        axios.get("http://localhost:3000/api/users/findUser/" + otherId)
            .then((u)=>{
                console.log(u)
            })


    })


    return (<>
        {chat.utente1 === loggedUserId ? <h2>{username}</h2> : <h2>{username}</h2>}
    </>)

}
