import Message from "./Message";
import {useEffect, useRef, useState} from "react";
import {socket} from "../socket";

export default function TextContainer({rightChat, loggedUser}) {
    const ref = useRef(null)
    const scroll = () => {
        const lastChild = ref.current?.lastElementChild;
        lastChild?.scrollIntoView()
    }

    useEffect(() => {
        socket.on("message", (message) => {
            const newMex = <Message key={message._id}
                                    message={message}
                                    loggedUser={loggedUser}></Message>

            let span = document.createElement("span")
            let color;
            loggedUser._id === message.sender[0] ? color = `red` : color = `green`
            span.innerHTML = message.text;
            span.style = {color: color}
            ref.current.append(p)
            console.log(`${ref.current}`)
            console.log(`${typeof ref.current}`)
            //console.log(`${typeof ref}`)
        })
        scroll()
    }, []);

    return (
        <div ref={ref}>
            {rightChat.messages.map((message) => {
                return <Message key={message._id}
                                message={message}
                                loggedUser={loggedUser}></Message>
            })}</div>


    )
}
