import Message from "./Message";

export default function Chat({rightChat, otherUser, loggedUser}) {
    return (<>
        <h1>{otherUser}</h1>

        <div className="text-container" scrollTop={scrollHeight}>
            {rightChat.messages.map((message) => {
                return <Message key={message._id}
                                message={message}
                                loggedUser={loggedUser}></Message>
            })}
        </div>

    </>)
}
