import {useEffect, useState} from "react";
import axios from "axios";

export default function Chat({chat, loggedUserId}) {

    const [username, setUsername] = useState(``)

    useEffect(() => {
        if (chat.utente1===loggedUserId){setUsername(chat.utente2)}else {setUsername(chat.utente1)}

        axios.get("http://localhost:3000/api/users/findUser/"+`${username}`).then((res) => {
            if (res.status === 200) {
                return res.data
            } else {
                throw new Error("Errore del server")
            }
        })
    }, [])


    return (<>
        {chat.utente1 === loggedUserId ? <h2>{chat.utente2}</h2> : <h2>{chat.utente1}</h2>}
    </>)

}
