import ChatContainer from "../components/ChatsContainer";
import React, {useEffect, useState} from "react";
import axios from "axios";
import Sidebar from "../components/Sidebar";
import ChatButton from "../components/ChatButton";

export default function ChatsPage() {
    const [loading, setLoading] = useState(true)
    const [error, setError] = useState(false)
    const [chats, setChats] = useState([])
    const [showChatRoom, setShowChatRoom]=useState(false)
    const loggedUser = JSON.parse(localStorage.getItem("user"))

    const onClick=()=>{
        setShowChatRoom(true)
        console.log(showChatRoom)
    }

    useEffect(() => {
        axios.get(`http://localhost:3000/api/users/showChat/` + `${loggedUser._id}`)
            .then((res) => {
                if (res.status === 200) {
                    return res.data
                } else {
                    throw new Error("Errore di comunicazione col server nell'ottenimento delle chat")
                }
            })
            .then((dati) => {
                setLoading(false);
                setChats(dati)
            })
            .catch((e) => {
                setError(true);
                setLoading(false);
                console.log(e)
            })
    }, [])
    return (
        <>
        <div>
            {loading ? <p>Caricamento in corso...</p> : error ? <p>Errore!!!Impossibile visualizzare le chat</p> : (
                <div className="sidebar">
                    {chats.map((chat)=>{
                        let user;
                        {loggedUser===chat.utente1? user=chat.utente2:user=chat.utente2}
                        return <ChatButton user={user}></ChatButton>
                    })}
                </div>
            )}
        </div>

    </>
    )

}
