import {useEffect, useState} from "react";
import axios from "axios";
import ChatButton from "./ChatButton";

export default function Chat({chat, loggedUserId, otherId}) {

    const [username, setUsername] = useState(``)

    useEffect(() => {
        axios.get("http://localhost:3000/api/users/findUser/" + otherId)
            .then((res) => {
                if (res.status === 200) {
                    return res.data
                } else {
                    throw new Error("Errore di comunicazione col server nell'ottenimento delle chat")
                }
            })
            .then((dati) => {
                setUsername(dati)
            })
            .catch((e) => {
                console.log(e)
            })


    }, [])


    return (<>
        <ChatButton description={username} url="/chatPanel/chatRoom"></ChatButton>
    </>)

}
