import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import Chat from "../components/Chat";
import ChatButton from "../components/ChatButton"
import '../style/style_viewfriends.css'


export default function ChatsPageFriends() {
    const [friendList, setFriendList] = useState([])
    const [selectedFriend, setSelectedFriend] = useState(null)
    const [loading, setLoading] = useState(true)
    const [error, setError] = useState(false)
    const [chats, setChats] = useState([])
    const [showChatRoom, setShowChatRoom] = useState(false)
    const [rightChat, setRightChat] = useState(Object)
    const [otherUser, setOtherUser]=useState(``)
    const loggedUser = JSON.parse(localStorage.getItem("user"))

    useEffect(() => {
        const user = JSON.parse(localStorage.getItem('user'))
        const me_user = user._id

        axios.post("http://localhost:3000/api/users/viewFriends", {  userId: me_user })
        .then(res => {
            if (res.data && res.data.friends) {
                setFriendList(res.data.friends) } })
        .catch((error) => {
            if (error.response && error.response.status === 401) {
                alert("Utente non trovato"); } 
              else {
                console.error(error);} })
                 }, [])

                 const handleFriendSelection = (friend) => {
                    setSelectedFriend(friend);
                  }


return (

    <div>
    <h2>In questa pagina puoi visualizzare i tuoi amici</h2>
    {friendList?.length === 0 ? (
      <> <p> Non sono presenti amici nella lista</p>
      <p> Se vuoi aggiungerli </p> 
      <a href='http://localhost:3001/addFriend'>
            <button> Aggiungi amico </button>
        </a> </>
    ) : (
        <>
        <ul className="friend-list">
        {friendList.map((friend) => (
          <li className="friend-list-item" key={friend._id} onClick={() => handleFriendSelection(friend)}>{friend.username}</li>
        )
        )}
      </ul>

 </> 
      )}
    <p> Per ritornare alla <a href="http://localhost:3001/homepage"> Homepage </a> </p> 
  </div>

    )
    
}
