import {useEffect, useState} from "react";
import axios from "axios";

export default function Chat({chat, loggedUserId}) {

    const [username, setUsername] = useState(``)
    const [otherId, setOtherId] = useState(``)

    useEffect(() => {
        if (chat.utente1 === loggedUserId) {
            setOtherId(chat.utente2)
        } else {
            setOtherId(chat.utente1)
        }

        axios.get("http://localhost:3000/api/users/findUser/" + otherId)
            .then((res) => {
                if (res.status === 200) {
                    return res.data
                } else {
                    throw new Error("Errore del server")
                }
            }).then((dati) => {
            setUsername(dati);
        })
    }, [])


    return (<>
        {chat.utente1 === loggedUserId ? <h2>{chat.utente2}</h2> : <h2>{chat.utente1}</h2>}
    </>)

}
