import TextContainer from "./TextContainer";
import SendMessage from "./SendMessage";
import {useEffect, useState} from "react";

export default function Chat({rightChat, otherUser, loggedUser}) {
    const [chats,setChats]=useState([])

    useEffect(()=>{
        setChats(rightChat)
    },[])
    return (<>
        <h1>{otherUser}</h1>
        <TextContainer rightChat={chats} loggedUser={loggedUser}></TextContainer>
        <SendMessage receiver={otherUser} sender={loggedUser} rightChat={chats} addText={setChats}></SendMessage>
    </>)
}
